import unittest
import day2


class TestDay2(unittest.TestCase):
    def test_defaultTest(self):
        test_case = "ULL\n" \
                    "RRDDD\n" \
                    "LURDL\n" \
                    "UUUUD"
        answ = day2.Day2().solve(test_case)

        self.assertEqual(answ, "5DB3")

    def test_defaultInput(self):
        test_case = "\n" \
                    "\n" \
                    "\n" \
                    "\n" \
                    ""

        answ = day2.Day2().solve(test_case)

        self.assertEqual(answ, "1985")
